function p = bissection(f, a, b, atol)  f = inline(f);  fa = f(a);  fb = f(b);  nIterations = 0;      while (b - a >= 2*atol)      nIterations++;        p = (a + b)/2;        fp = f(p);        if (fp * fa < 0)      b = p;      fb = fp;    elseif (fp * fb < 0)      a = p;      fa = fp;    else      return;    endif    endwhile      disp(nIterations);   disp(b - a);   p = (a + b)/2;    endfunction